import React, { useState } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useApp } from '../../../contexts/AppContext';
import { useAuth } from '../../../contexts/AuthContext';
import { createCompleteFabricEntry } from '../../../services/fabric.service';
import { QUANTITY_UNITS, FABRIC_TYPES, UAT_UNITS } from '../../../utils/constants';
import RollRow from './RollRow';
import RibDetails from './RibDetails';
import FileUpload from './FileUpload';

// Form validation schema
const fabricEntrySchema = yup.object().shape({
  seller_name: yup.string().required('Seller name is required'),
  quantity_value: yup.number().required('Quantity is required').positive('Must be positive'),
  quantity_unit: yup.string().oneOf(QUANTITY_UNITS).required('Unit is required'),
  color: yup.string().required('Color is required'),
  fabric_type: yup.string().oneOf(FABRIC_TYPES).required('Fabric type is required'),
  po_number: yup.string().required('PO Number is required'),
  fabric_composition: yup.string().required('Fabric composition is required'),
  inwarded_by: yup.string().required('Inwarded by is required'),
  uat_value: yup.number().required('UAT value is required').positive('Must be positive'),
  uat_unit: yup.string().oneOf(UAT_UNITS).required('UAT unit is required'),
  rolls: yup.array().of(
    yup.object().shape({
      roll_value: yup.number().required('Roll value is required').positive('Must be positive'),
      roll_unit: yup.string().oneOf(['KG', 'METER']).required('Roll unit is required'),
    })
  ).min(1, 'At least one roll is required'),
});

interface InwardFormData {
  seller_name: string;
  quantity_value: number;
  quantity_unit: string;
  color: string;
  fabric_type: string;
  po_number: string;
  fabric_composition: string;
  inwarded_by: string;
  uat_value: number;
  uat_unit: string;
  rolls: Array<{
    roll_value: number;
    roll_unit: string;
  }>;
  rib_details?: {
    total_weight?: number;
    total_rolls?: number;
  };
  ftp_document?: File;
}

const InwardForm: React.FC = () => {
  const { addNotification } = useApp();
  const { user } = useAuth();
  const [showRibDetails, setShowRibDetails] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const {
    register,
    control,
    handleSubmit,
    reset,
    formState: { errors },
    setValue,
    watch,
  } = useForm<InwardFormData>({
    resolver: yupResolver(fabricEntrySchema),
    defaultValues: {
      rolls: [{ roll_value: 0, roll_unit: 'KG' }],
      inwarded_by: user?.email || '',
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'rolls',
  });

  const addRoll = () => {
    append({ roll_value: 0, roll_unit: 'KG' });
  };

  const removeRoll = (index: number) => {
    if (fields.length > 1) {
      remove(index);
    }
  };

  const onSubmit = async (data: InwardFormData) => {
    setIsSubmitting(true);
    try {
      // Prepare fabric entry data
      const fabricEntryData = {
        seller_name: data.seller_name,
        quantity_value: data.quantity_value,
        quantity_unit: data.quantity_unit as any,
        color: data.color,
        fabric_type: data.fabric_type as any,
        po_number: data.po_number,
        fabric_composition: data.fabric_composition,
        inwarded_by: data.inwarded_by,
        uat_value: data.uat_value,
        uat_unit: data.uat_unit as any,
        date_inwarded: new Date().toISOString().split('T')[0],
        status: 'PENDING_QUALITY' as any,
      };

      // Prepare complete form data
      const completeFormData = {
        fabricEntry: fabricEntryData,
        rolls: data.rolls,
        ribDetails: data.rib_details,
        file: data.ftp_document,
      };

      const { data: fabricEntry, error } = await createCompleteFabricEntry(completeFormData);

      if (error) {
        throw new Error(typeof error === 'string' ? error : 'Failed to create fabric entry');
      }

      addNotification({
        type: 'success',
        message: `Fabric entry created successfully with ID: ${fabricEntry?.[0]?.id}`,
      });

      // Reset form
      reset();
      setShowRibDetails(false);

    } catch (error) {
      addNotification({
        type: 'error',
        message: error instanceof Error ? error.message : 'Failed to create fabric entry',
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const currentDate = new Date().toLocaleDateString();

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="space-y-8">
      {/* Main Form Fields */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-6">Fabric Entry Details</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Seller Name */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Seller Name *
            </label>
            <input
              type="text"
              {...register('seller_name')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter seller name"
            />
            {errors.seller_name && (
              <p className="mt-1 text-sm text-red-600">{errors.seller_name.message}</p>
            )}
          </div>

          {/* Quantity */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Quantity *
            </label>
            <div className="flex space-x-2">
              <input
                type="number"
                step="0.01"
                {...register('quantity_value')}
                className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="0.00"
              />
              <select
                {...register('quantity_unit')}
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {QUANTITY_UNITS.map(unit => (
                  <option key={unit} value={unit}>{unit}</option>
                ))}
              </select>
            </div>
            {errors.quantity_value && (
              <p className="mt-1 text-sm text-red-600">{errors.quantity_value.message}</p>
            )}
          </div>

          {/* Color */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Color *
            </label>
            <input
              type="text"
              {...register('color')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter color"
            />
            {errors.color && (
              <p className="mt-1 text-sm text-red-600">{errors.color.message}</p>
            )}
          </div>

          {/* Fabric Type */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Fabric Type *
            </label>
            <select
              {...register('fabric_type')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">Select fabric type</option>
              {FABRIC_TYPES.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
            {errors.fabric_type && (
              <p className="mt-1 text-sm text-red-600">{errors.fabric_type.message}</p>
            )}
          </div>

          {/* PO Number */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              PO Number *
            </label>
            <input
              type="text"
              {...register('po_number')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter PO number"
            />
            {errors.po_number && (
              <p className="mt-1 text-sm text-red-600">{errors.po_number.message}</p>
            )}
          </div>

          {/* Date Inwarded (Display Only) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Date Inwarded
            </label>
            <input
              type="text"
              value={currentDate}
              disabled
              className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-gray-500"
            />
          </div>

          {/* Inwarded By */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Inwarded By *
            </label>
            <input
              type="text"
              {...register('inwarded_by')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter name"
            />
            {errors.inwarded_by && (
              <p className="mt-1 text-sm text-red-600">{errors.inwarded_by.message}</p>
            )}
          </div>

          {/* UAT */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              UAT *
            </label>
            <div className="flex space-x-2">
              <input
                type="number"
                step="0.01"
                {...register('uat_value')}
                className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="0.00"
              />
              <select
                {...register('uat_unit')}
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {UAT_UNITS.map(unit => (
                  <option key={unit} value={unit}>{unit}</option>
                ))}
              </select>
            </div>
            {errors.uat_value && (
              <p className="mt-1 text-sm text-red-600">{errors.uat_value.message}</p>
            )}
          </div>
        </div>

        {/* Fabric Composition */}
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Fabric Composition *
          </label>
          <textarea
            {...register('fabric_composition')}
            rows={3}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter fabric composition details"
          />
          {errors.fabric_composition && (
            <p className="mt-1 text-sm text-red-600">{errors.fabric_composition.message}</p>
          )}
        </div>
      </div>

      {/* Roll Details */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-6">Roll Details</h2>

        <div className="space-y-4">
          {fields.map((field, index) => (
            <RollRow
              key={field.id}
              index={index}
              register={register}
              errors={errors}
              onRemove={() => removeRoll(index)}
              canRemove={fields.length > 1}
              batchNumber={index + 1}
            />
          ))}
        </div>

        <button
          type="button"
          onClick={addRoll}
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          Add Roll
        </button>

        {errors.rolls && (
          <p className="mt-2 text-sm text-red-600">{errors.rolls.message}</p>
        )}
      </div>

      {/* Optional Sections */}
      <div className="space-y-6">
        {/* Rib Details Toggle */}
        {!showRibDetails && (
          <button
            type="button"
            onClick={() => setShowRibDetails(true)}
            className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
          >
            Add Rib Details
          </button>
        )}

        {/* Rib Details Component */}
        {showRibDetails && (
          <RibDetails
            register={register}
            errors={errors}
            onClose={() => setShowRibDetails(false)}
          />
        )}

        {/* File Upload */}
        <FileUpload
          onFileSelect={(file: File | null) => setValue('ftp_document', file || undefined)}
          label="Attach FTP Document (Optional)"
          accept=".pdf"
        />
      </div>

      {/* Submit Buttons */}
      <div className="flex justify-end space-x-4">
        <button
          type="button"
          onClick={() => {
            reset();
            setShowRibDetails(false);
          }}
          className="px-6 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
        >
          Reset
        </button>
        <button
          type="submit"
          disabled={isSubmitting}
          className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isSubmitting ? 'Saving...' : 'Save Entry'}
        </button>
      </div>
    </form>
  );
};

export default InwardForm;
